import React, { useState } from "react";
import { Link } from "react-router-dom";

//import { TweenLite, TweenMax, TimelineLite, TimelineMax,  } from "gsap";
//import { Tween, Timeline } from 'react-gsap';
//import { library } from '@fortawesome/fontawesome-svg-core'
//import { fas } from '@fortawesome/free-solid-svg-icons'
//import { fab } from '@fortawesome/free-brands-svg-icons'
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import { SlideUp } from "./NavBar/SlideUp";

export const NavBar = () => {
  let [slideUpState, setSlideUpState] = useState("hideit");
  let [hambugerState, setHambugerState] = useState("menuBarLeft");
  let [xToggleState, setXToggleState] = useState("hideit");

  return (
    <>
    <div className={slideUpState}>
      <i onClick=
        {() => {
          setSlideUpState("animate-bottom-rev toggleMainBottomHide");
          setHambugerState("menuBarLeft");
          setXToggleState("hideit")
        }}
        className="fas fa-times xInSlidUp"
      ></i>

      <section>
        <h2>Full Stack Web Developer In Training</h2>
        <p>An experienced old school LAMP developer looking to update their knowledge, and expand their Full Stack skill sets to modern standards. I look forward to putting my experience, knowledge, and skillsets to work solving whatever problems you need help with. </p>
      </section>

      <p>Show Me</p>
      <p>Show Me</p>
      <p>Show Me</p>
      <p>Show Me</p>
      <p>Show Me</p>
      <SlideUp />
    </div>


    <nav>
      <div className="menuBar">

        <div className={hambugerState}>
          <i onClick=
            {() => {
              setSlideUpState("w3-animate-bottom toggleMainBottom");
              setHambugerState("hideit");
              setXToggleState("menuBarLeft")
            }}
            className="fas fa-bars"
          ></i>
          <div className="nameBox"
            onClick=
              {() => {
                setSlideUpState("w3-animate-bottom  toggleMainBottom");
                setHambugerState("hideit");
                setXToggleState("menuBarLeft")
              }}
            >
            Russ Hacker
          </div>
        </div>

        <div className={xToggleState}>
          <i onClick=
            {() => {
              setSlideUpState("animate-bottom-rev toggleMainBottomHide");
              setHambugerState("menuBarLeft");
              setXToggleState("hideit")
            }}
            className="fas fa-times"
          ></i>
          <div className="nameBox"
            onClick=
              {() => {
                setSlideUpState("animate-bottom-rev toggleMainBottomHide");
                setHambugerState("menuBarLeft");
                setXToggleState("hideit")
              }}
            >
            Russ Hacker
          </div>
        </div>



        <div className="menuBarRight">
          <Link to="https://github.com/newbi462">
            <i className="fab fa-github"></i>
          </Link>
          <Link to="https://www.linkedin.com/in/russell-h-bb049256/">
            <i className="fab fa-linkedin"></i>
          </Link>
        </div>
      </div>


    </nav>
    </>
  );
};








